def CHECK_PRIME(NUMBER):
    # Check if the number is greater than 1
    if NUMBER > 1:
        # Iterate from 2 to NUMBER-1
        for I in range(2, NUMBER):
            # If NUMBER is divisible by any number between 2 and NUMBER-1, it is not prime
            if (NUMBER % I) == 0:
                return False
        # If no divisors were found, NUMBER is prime
        return True
    else:
        # If NUMBER is less than or equal to 1, it is not prime
        return False

if __name__ == '__main__':
    # Get user input and convert it to an integer
    USER_INPUT = int(input("Enter a number: "))
    # Print whether the input number is prime
    print(f"Is {USER_INPUT} prime? {CHECK_PRIME(USER_INPUT)}")
